package Assignment;
// Swap Number
public class Assignment4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int a=1,b=2,c;
 System.out.println("*******before swap*******");
 System.out.println("a="+a);
 System.out.println("b="+b);
     c=a;
     a=b;
     b=c;
     System.out.println("*******after swap******");
     System.out.println("a="+a);
     System.out.println("b="+b);
   
    		 
	}

}
