package WhileLoop;

public class WhileLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int i= 0;
 System.out.println("Printing number from 0 to 4");
 while (i<5) {
	 System.out.println("hello"+i);
	 i++;
	  }
 int a=5;
 while(a>0) {
	 System.out.println("bye"+a);
	 a--;
 }
	}

}
