package day2variableprogramExample1;

public class Example2 {
	static int age;
	static boolean res;
	static char grade;		
    static float roi;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Program Starts");
		System.out.println("default value of age is :"+age);
		System.out.println("default value of boolean is :"+res);
		System.out.println("default value of char is :"+grade);
		System.out.println("default value of float is :"+roi);
		System.out.println("********************");
		age=25;
		res=true;
		grade='A';
		roi=6.5f;
		System.out.println("update value of age is :"+age);
		System.out.println("update value of res is :"+res);
		System.out.println("update value of grade is :"+grade);
		System.out.println("update value of float is :"+roi);
		System.out.println("Program Ends..");
		
		
		
		
		
		
		
		
		
		
		

	}

}
